mapper.insert:
insert into products(
	product_name, brand_name, price, discount, product_stock, introduce, product_detail, model_code, origin, sub_category_id, seller_id
)
values (
	#{productName}, #{brandName}, #{price}, #{discount}, #{productStock}, #{introduce}, #{productDetail}, #{modelCode}, #{origin}, #{subCategoryId}, #{sellerId}
)

mapper.update:
update products
set
	product_name = #{productName}
	, brand_name = #{brandName}
	, price = #{price}
	, discount = #{discount}
	, product_stock = #{productStock}
	, introduce = #{introduce}
	, product_detail = #{productDetail}
	, model_code = #{modelCode}
	, origin = #{origin}
	, sub_category_id = #{subCategoryId}
where product_id = #{productId}
