mapper.insert:
insert into sellers (
email, password, name, tel, business_num, zip_code, address, detail_address
)
values (
#{email},#{password},#{name},#{tel},#{businessNum}, #{zipCode}, #{address}, #{detailAddress}
)

mapper.update:
update sellers
set 
	password = #{password}
	, name = #{name}
	, tel = #{tel}
	, zip_code = #{zipCode}
	, address = #{address}
	, detail_address = #{detailAddress}
	, business_num = #{businessNum}
where seller_id = #{sellerId}

update sellers
set password = #{password}
where email = #{email}
