mapper.insert:
insert into members (
	email, password, name, birth, phone, zip_code, address, detail_address, gender, role
)
values (
	#{email}, #{password}, #{name}, #{birth}, #{phone}, #{zipCode}, #{address}, #{detailAddress}, #{gender}, #{role}
)

mapper.update:
update members
set 
	name = #{name}
	, birth = #{birth}
	, phone = #{phone}
	, zip_code = #{zipCode}
	, address = #{address}
	, detail_address = #{detailAddress}
	, gender = #{gender}
where member_id = #{memberId}

mapper.delete:
update members
set 
	status = #{status}
where member_id = #{memberId}

mapper.update:
update members
set password = #{password}
where email = #{email}
