mapper.insert:
insert into sellers (
email,
password, name, tel, business_num, account_num, zip_code, address,
detail_address
)
values (
#{email},#{password},#{name},#{tel},#{businessNum}, #{businessNum},
#{zipCode}, #{address}, #{detailAddress}
)

mapper.update:
update sellers
set
password = #{password}
, name = #{name}
, tel = #{tel}
, zip_code = #{zipCode}
, address = #{address}
, detail_address = #{detailAddress}
, business_num = #{businessNum}
, account_num = #{accountNum}
where seller_id = #{sellerId}

update sellers
set status = #{status}, reject_msg = #{rejectMsg}
where seller_id = #{sellerId}

update sellers
set
password = #{password}
where email = #{email}

mapper.select:
select count(*) from sellers
where seller_id = #{sellerId}
and password = #{password}
