mapper.select:
select * from settlement 
where store_info_id = #{storeInfoId} 
and settlement_status = #{settlementStatus}

select * from settlement 
where  settlement_status = #{settlementStatus}
order by settlement_id desc

mapper.update:
update settlement set settlement_status = #{settlementStatus}, net_amount = #{netAmount}
, settlement_requested_at = NOW()
, settlement_completed_at = NOW()
where settlement_id = #{settlementId}

mapper.select:
SELECT COUNT(*)
FROM settlement
WHERE settlement_status = #{settlementStatus}
  AND store_info_id = #{storeInfoId}
  AND DATE_FORMAT(created_at, '%Y-%m') = #{yearMonth}

SELECT IFNULL(SUM(requested_amount), 0)
FROM settlement
WHERE settlement_status = #{settlementStatus}
  AND store_info_id = #{storeInfoId}
  AND DATE_FORMAT(created_at, '%Y-%m') = #{yearMonth}

SELECT COUNT(*)
FROM settlement
WHERE settlement_status = #{settlementStatus}
  AND DATE_FORMAT(created_at, '%Y-%m') = #{yearMonth}

SELECT IFNULL(SUM(requested_amount), 0)
FROM settlement
WHERE settlement_status = #{settlementStatus}
  AND DATE_FORMAT(created_at, '%Y-%m') = #{yearMonth}
